let conversationHistory = [
  { role: "system", content: `You are an assistant helping a user on an appliance part website, PartSelect.com. 
    You will not answer any questions not about appliances and PartSelect.com. You keep your responses helpful, short and generic.
    ` }
];

export const getAIMessage = async (userQuery) => {

  conversationHistory.push({ role: "user", content: userQuery });

  const completion = await fetch("https://api.openai.com/v1/chat/completions", {
    method: "POST",
    headers: {
        "Content-Type": "application/json",
        "Authorization": 'Bearer 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',
    },
    body: JSON.stringify({
        model: "gpt-4o-mini",
        messages: conversationHistory
    })
  });

  const data = await completion.json();
  let get_response = data.choices[0].message.content;
  console.log(get_response);
  conversationHistory.push({ role: "assistant", content: get_response });

  return get_response
};
