import React, { useState, useEffect, useRef } from "react";
import { marked } from "marked";
import "./ChatWindow.css";
import "./Message.css"

export function Message(role, content, link) {

    let html_text = marked(content)

    if (role == "assistant"){
        if (link) {
            return(
                <div className="assistant-message-container">
                    <div className="message assistant-message">
                    <div dangerouslySetInnerHTML={{ __html: html_text }} />
                        <div className="link-box"><a href={link}><img className="icon" src="https://static.thenounproject.com/png/2166724-200.png"/> Link to part </a></div>
                    </div>
                </div>
            )
        } else {
            return(
                <div className="assistant-message-container">
                    <div className="message assistant-message">
                        <div dangerouslySetInnerHTML={{ __html: html_text}}/>
                    </div>
                </div>
            )
        }
    } else {
        return(
            <div className="user-message-container">
                <div className="message user-message">
                    <div dangerouslySetInnerHTML={{ __html: html_text}} />
                </div>
            </div>
        )
    }
}
