let conversationHistoryID = [
  { role: "system", content: `If there was a part ID (number beginning with P) specified, give me that number. Respond with ONLY the relevant part ID number.` }
];

export const getID = async (userQuery) => {

  conversationHistoryID.push({ role: "user", content: userQuery });

  const completion = await fetch("https://api.openai.com/v1/chat/completions", {
    method: "POST",
    headers: {
        "Content-Type": "application/json",
        "Authorization": 'Bearer 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',
    },
    body: JSON.stringify({
        model: "gpt-4o-mini",
        messages: conversationHistoryID
    })
  });

  const data = await completion.json();
  let get_response = data.choices[0].message.content;
  console.log(get_response);
  conversationHistoryID.push({ role: "assistant", content: get_response });

  return get_response
};
