import {getInfo} from '../parts/get_part_info.js'
import OpenAI from "openai";

const openai = new OpenAI({apiKey: "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"});


export const getAIMessage = async (userQuery) => {

  let text = getInfo(userQuery)
  console.log(text)

  const completion = await openai.chat.completions.create({
      model: "gpt-4o",
      store: true,
      messages: [
          {"role": "user", "content": "write a haiku about ai"}
      ]
  });

  return completion;
};
