import axios from 'axios';
import { load } from 'cheerio';

 export async function scrapeText(url) {
    try {
      // Make an HTTP request to fetch the HTML of the page
      const { data } = await axios.get(url);

      // Load the HTML into Cheerio
      const $ = cheerio.load(data);

      // Example: Extract text from the body of the page
      // You can use specific selectors to target certain parts of the page
      const text = $('body').text(); // For all body text
      // Or, for specific content, use: $('.class-name') or $('#id-name')

      return text;
    } catch (error) {
      console.error('Error scraping the page:', error);
      return '';
    }
  }
