const api_key = "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";


let conversationHistory = [
  { role: "system", content: `You are an assistant helping a user for an appliance part website, PartSelect.com, find information about parts. 
    You will not answer any questions not about appliances. You are kind, helpful and knowledgeable.` }
];

export const getAIMessage = async (userQuery) => {

  conversationHistory.push({ role: "user", content: userQuery });

  const completion = await fetch("https://api.openai.com/v1/chat/completions", {
    method: "POST",
    headers: {
        "Content-Type": "application/json",
        "Authorization": 'Bearer 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',
    },
    body: JSON.stringify({
        model: "gpt-4o-mini",
        messages: [
            { role: "system", content: `You are an assistant helping a user for an appliance part website, PartSelect.com, find information about parts. 
              You will not answer any questions not about appliances. You are kind, helpful and knowledgeable.` },
            {
                role: "user",
                content: conversationHistory
            },
        ]
    })
  });

  const data = await completion.json();
  let get_response = data.choices[0].message.content;
  console.log(get_response);
  conversationHistory.push({ role: "assistant", content: get_response });

  return get_response
};
